sourcesContent:
const { DataTypes, Model } = require('sequelize');
const { sequelize } = require('../config/db');
const User = require('../User');


class OtpCode extends Model {}


OtpCode.init({
id: { type: DataTypes.UUID, defaultValue: DataTypes.UUIDV4, primaryKey: true },
userId: { type: DataTypes.UUID, allowNull: false, references: { model: 'users', key: 'id' } },
purpose: { type: DataTypes.ENUM('REGISTER','RESET_PASSWORD'), allowNull: false },
codeHash: { type: DataTypes.STRING(100), allowNull: false },
expiresAt: { type: DataTypes.DATE, allowNull: false },
consumedAt: { type: DataTypes.DATE },
}, { sequelize, modelName: 'OtpCode', tableName: 'otp_codes', underscored: true });


OtpCode.belongsTo(User, { foreignKey: 'userId' });


module.exports = OtpCode;

