var express = require("express");
var cors = require("cors");
var cookieParser = require("cookie-parser");
var rateLimit = require("express-rate-limit");
require("dotenv").config();
var _require = require("./config/configdb"),
  sequelize = _require.sequelize;
var authRoutes = require('./routes/auth.routes');
var userRoutes = require('./routes/user.routes');
var _require2 = require("./middleware/errorHandler"),
  errorHandler = _require2.errorHandler;
var app = express();
app.use(cors({
  origin: true,
  credentials: true
}));
app.use(express.json());
app.use(cookieParser());

// Rate limit
var otpLimiter = rateLimit({
  windowMs: 60 * 1000,
  max: 5
});
var loginLimiter = rateLimit({
  windowMs: 15 * 60 * 1000,
  max: 20
});
app.use("/api/auth/register", otpLimiter);
app.use("/api/auth/forgot", otpLimiter);
app.use("/api/auth/login", loginLimiter);

